import { createDirectus, rest, readSingleton,createItem,deleteItem } from '@directus/sdk';
const client = createDirectus('http://localhost:8055').with(rest());
export let $categorieslist=async()=>{
    let result = await client.request(readSingleton('categories'));
    return result
}
export let $addProduct=async(params)=>{
let result = await client.request(
	createItem('products' ,params))
    // if(succes){
    //     message.success(msg)
    // }
    console.log(result);
}

export let $addCate=async(params)=>{
const result = await client.request(
	createItem('products_categories', params)
)}

export let $delProduct=async(params)=>{
    const result = await client.request(deleteItem('products', params))
}
