import { createDirectus, rest, readSingleton, createItem, deleteItem, readItem } from '@directus/sdk';
const client = createDirectus('http://localhost:8055').with(rest());
export let $categorieslist = async () => {
    let result = await client.request(readSingleton('categories'));
    return result
}
export let $addProduct = async (params) => {
    let result = await client.request(
        createItem('products', params))
    // if(succes){
    //     message.success(msg)
    // }
    console.log(result);
}

