      }
    };
    let imglist = [];
    // 4️⃣ 处理文件拖拽事件
    function handleDrop(e) {
      // 获取拖拽的文件
      const files = e.dataTransfer.files;
      if (files.length > 0) {
        // 使用 FormData 来构建上传的文件数据
        const formData = new FormData();
        for (let file of files) {
          formData.append('file', file);
        }
        // console.log(formData);

        // 确保 imageUrl 是 Vue 响应式的 ref 类型
