        // 上传文件处理逻辑
        client.request(uploadFiles(formData)).then(response => {
          console.log('Response:', response); // 检查 response 数据结构
          const imageUrlPath = `http://localhost:8055/assets/${response.id}`;

          // 使用 .value 来赋值，因为 imageUrl 是一个 ref 类型
          imageUrl.value = imageUrlPath;
        }).catch(error => {
          // 上传失败处理
