      if (status !== 'uploading') {
        console.log(info.file, info.fileList);
      }
      if (status === 'done') {
        message.success(`${info.file.name} 成功上传.`);
      } else if (status === 'error') {
        message.error(`${info.file.name} 上传失败.`);
      }
    };
    function handleDrop(e) {
      const files = e.dataTransfer.files;
      if (files.length > 0) {
        // 使用 FormData 来构建上传的文件数据
