import "core-js/modules/es.array.with.js";
import AddProduct from '../../components/product/addProduct';
import { reactive, toRefs, ref } from 'vue';
import { createDirectus, rest, readItems } from '@directus/sdk';
const showDrawer = false;
// 数据源

export default {
  __name: 'productsinfo',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const searchKeyword = ref('');
    let dataSource = reactive({
      tableDatas: [],
      // 确保 dataSource.tableDatas 一开始是空数组
      tableColumns: [{
        title: '产品id',
        dataIndex: 'id',
        key: 'id'
      }, {
        title: '产品名称',
        dataIndex: 'name',
        key: 'name'
      }, {
        title: '价格',
        dataIndex: 'price',
        key: 'price'
      }, {
        title: '类别',
        dataIndex: 'sort',
        key: 'sort'
      }]
    });
    // 使用 `toRefs` 解构 `reactive` 数据，方便在模板中直接使用
    let {
      tableDatas,
      tableColumns
    } = toRefs(dataSource);
    const client = createDirectus('http://localhost:8055').with(rest());
    let loadData = async (searchKeyword = '') => {
      try {
        const filter = searchKeyword ? {
          filter: {
            name: {
              _contains: searchKeyword // 直接查询包含关键字的产品
            }
          }
        } : {};
        const result = await client.request(readItems('products', {
          fields: ['*'],
          ...filter // 传入过滤条件
        }));

        // 将返回的数据赋值给 tableDatas
        tableDatas.value = result;
      } catch (error) {
        console.error('Failed to fetch items:', error);
      }
    };
    loadData();
    let onSearch = value => {
      searchKeyword.value = value;
      loadData(value); // 传递搜索内容
    };
    const __returned__ = {
      searchKeyword,
      showDrawer,
      get dataSource() {
        return dataSource;
      },
      set dataSource(v) {
        dataSource = v;
      },
      get tableDatas() {
        return tableDatas;
      },
      set tableDatas(v) {
        tableDatas = v;
      },
      get tableColumns() {
        return tableColumns;
      },
      set tableColumns(v) {
        tableColumns = v;
      },
      client,
      get loadData() {
        return loadData;
      },
      set loadData(v) {
        loadData = v;
      },
      get onSearch() {
        return onSearch;
      },
      set onSearch(v) {
        onSearch = v;
      },
      get AddProduct() {
        return AddProduct;
      },
      reactive,
      toRefs,
      ref,
      get createDirectus() {
        return createDirectus;
      },
      get rest() {
        return rest;
      },
      get readItems() {
        return readItems;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
