import "core-js/modules/es.array.with.js";
import { ref } from 'vue';
import { message } from 'ant-design-vue';
import { InboxOutlined } from '@ant-design/icons-vue';
import { createDirectus, rest, uploadFiles } from '@directus/sdk';

// 创建 Directus 客户端

export default {
  __name: 'ocr',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const client = createDirectus('http://localhost:8055').with(rest());

    // 定义 Vue 响应式数据
    const fileList = ref([]);
    const uploadStatus = ref('');
    const imageUrl = ref(''); // ✅ 确保全局可访问

    // 文件上传状态处理
    const handleChange = info => {
      const status = info.file.status;
      if (status !== 'uploading') {
        console.log(info.file, info.fileList);
      }
      if (status === 'done') {
        message.success(`${info.file.name} file uploaded successfully.`);
      } else if (status === 'error') {
        message.error(`${info.file.name} file upload failed.`);
      }
    };

    // 处理文件拖拽上传
    const handleDrop = async e => {
      const files = e.dataTransfer.files;
      if (files.length === 0) return;
      const formData = new FormData();
      for (let file of files) {
        formData.append('file', file);
      }
      try {
        console.log('Uploading file:', formData);
        const response = await client.request(uploadFiles(formData));
        console.log('Upload Response:', response);
        if (!response || !response.id) {
          console.error('Invalid response:', response);
          message.error('Upload failed: No valid file ID received.');
          return;
        }

        // 生成 Directus 资源 URL
        imageUrl.value = `http://localhost:8055/assets/${response.id}`;
        console.log('Image URL:', imageUrl.value);
        message.success('File uploaded successfully!');
      } catch (error) {
        console.error('Upload failed:', error);
        message.error('File upload failed!');
      }
    };
    const __returned__ = {
      client,
      fileList,
      uploadStatus,
      imageUrl,
      handleChange,
      handleDrop,
      ref,
      get message() {
        return message;
      },
      get InboxOutlined() {
        return InboxOutlined;
      },
      get createDirectus() {
        return createDirectus;
      },
      get rest() {
        return rest;
      },
      get uploadFiles() {
        return uploadFiles;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
