import { reactive, ref } from 'vue';

// 定义 props 和 emits

export default {
  __name: 'addProduct',
  props: {
    showDrawer: Boolean // 从父组件接收 showDrawer
  },
  emits: ['update:showDrawer'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();
    const props = __props;
    const emit = __emit; // 向父组件发出更新事件

    // 表单数据和验证规则
    const form = reactive({
      name: '',
      url: '',
      owner: '',
      type: '',
      approver: '',
      dateTime: null,
      description: ''
    });
    const rules = {
      name: [{
        required: true,
        message: 'Please enter user name'
      }],
      url: [{
        required: true,
        message: 'please enter url'
      }],
      owner: [{
        required: true,
        message: 'Please select an owner'
      }],
      type: [{
        required: true,
        message: 'Please choose the type'
      }],
      approver: [{
        required: true,
        message: 'Please choose the approver'
      }],
      dateTime: [{
        required: true,
        message: 'Please choose the dateTime',
        type: 'object'
      }],
      description: [{
        required: true,
        message: 'Please enter url description'
      }]
    };

    // 控制抽屉开关的变量
    const open = ref(props.showDrawer); // 初始时，open 由父组件传入的 showDrawer 控制
    // 关闭抽屉并同步父组件状态
    const onClose = () => {
      open.value = false;
    };

    // 监听抽屉的状态变化并更新父组件的 showDrawer 状态
    const afterDrawerChange = val => {
      if (!val) {
        emit('update:showDrawer', false); // 当抽屉关闭时，更新父组件的 showDrawer 为 false
      }
    };
    const __returned__ = {
      props,
      emit,
      form,
      rules,
      open,
      onClose,
      afterDrawerChange,
      reactive,
      ref
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
