import "core-js/modules/es.array.with.js";
import { reactive, toRefs, onMounted } from 'vue';
import { createDirectus, rest, readItems } from '@directus/sdk';

// 数据源

export default {
  __name: 'productsinfo',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    let dataSource = reactive({
      tableDatas: [],
      // 确保 dataSource.tableDatas 一开始是空数组
      tableColumns: [{
        title: '产品id',
        dataIndex: 'id',
        key: 'id'
      }, {
        title: '产品名称',
        dataIndex: 'name',
        key: 'name'
      }, {
        title: '价格',
        dataIndex: 'price',
        key: 'price'
      }, {
        title: '类别',
        dataIndex: 'sort',
        key: 'sort'
      }]
    });

    // 使用 `toRefs` 解构 `reactive` 数据，方便在模板中直接使用
    let {
      tableDatas,
      tableColumns
    } = toRefs(dataSource);
    const client = createDirectus('http://localhost:8055').with(rest());

    // 数据存储和状态管理
    onMounted(async () => {
      await fetchItems();
    });

    // 获取产品数据
    async function fetchItems() {
      try {
        const result = await client.request(readItems('products', {
          fields: ['*']
        }));
        console.log(result);

        // 将返回的数据赋值给 tableDatas
        tableDatas.value = result;
      } catch (error) {
        console.error('Failed to fetch items:', error);
      }
    }
    ;
    const __returned__ = {
      get dataSource() {
        return dataSource;
      },
      set dataSource(v) {
        dataSource = v;
      },
      get tableDatas() {
        return tableDatas;
      },
      set tableDatas(v) {
        tableDatas = v;
      },
      get tableColumns() {
        return tableColumns;
      },
      set tableColumns(v) {
        tableColumns = v;
      },
      client,
      fetchItems,
      reactive,
      toRefs,
      onMounted,
      get createDirectus() {
        return createDirectus;
      },
      get rest() {
        return rest;
      },
      get readItems() {
        return readItems;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
