import "core-js/modules/es.array.with.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.map.js";
import { reactive, toRefs, onMounted } from 'vue';
import { createDirectus, rest, readItems, ref } from '@directus/sdk';

// 数据源

export default {
  __name: 'productsinfo',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    let dataSource = reactive({
      tableDatas: [],
      // 确保 dataSource.tableDatas 一开始是空数组
      tableColumns: [{
        title: '产品id',
        dataIndex: 'id',
        key: 'id'
      }, {
        title: '产品名称',
        dataIndex: 'name',
        key: 'name'
      }, {
        title: '价格',
        dataIndex: 'price',
        key: 'price'
      }, {
        title: '类别',
        dataIndex: 'sort',
        key: 'sort'
      }]
    });

    // 使用 `toRefs` 解构 `reactive` 数据，方便在模板中直接使用
    let {
      tableDatas,
      tableColumns
    } = toRefs(dataSource);

    // 创建 Directus 客户端实例
    const client = createDirectus('http://localhost:8055').with(rest());

    // 获取产品数据
    async function fetchItems() {
      try {
        const result = await client.request(readItems('products', {
          fields: ['*'] // 获取所有字段
        }));
        // 确保获取到的数据为数组格式
        tableDatas.value = Array.isArray(result) ? result.data.map(item => ({
          key: item.id,
          // 使用 id 作为 key
          id: item.id,
          name: item.name,
          price: item.price,
          sort: item.sort
        })) : [];
        console.log(tableDatas.value); // 如果返回的数据不是数组，使用空数组
      } catch (error) {
        console.error('Failed to fetch items:', error);
      }
    }
    ;

    // 在组件挂载时调用 fetchItems 获取数据
    // onMounted(() => {
    //     fetchItems();
    // });

    const __returned__ = {
      get dataSource() {
        return dataSource;
      },
      set dataSource(v) {
        dataSource = v;
      },
      get tableDatas() {
        return tableDatas;
      },
      set tableDatas(v) {
        tableDatas = v;
      },
      get tableColumns() {
        return tableColumns;
      },
      set tableColumns(v) {
        tableColumns = v;
      },
      client,
      fetchItems,
      reactive,
      toRefs,
      onMounted,
      get createDirectus() {
        return createDirectus;
      },
      get rest() {
        return rest;
      },
      get readItems() {
        return readItems;
      },
      get ref() {
        return ref;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
