import "core-js/modules/es.array.with.js";
import { ref } from 'vue';
import { message } from 'ant-design-vue';
import { InboxOutlined } from '@ant-design/icons-vue';
import { createDirectus, rest, uploadFiles } from '@directus/sdk';

// 1️⃣ 创建 Directus 客户端

export default {
  __name: 'ocr',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const client = createDirectus('http://localhost:8055').with(rest());

    // 2️⃣ 初始化文件列表和上传状态
    const fileList = ref([]);
    let uploadStatus = ref('');

    // 3️⃣ 文件上传成功/失败时的处理
    const handleChange = info => {
      const status = info.file.status;
      if (status !== 'uploading') {
        console.log(info.file, info.fileList);
      }
      if (status === 'done') {
        message.success(`${info.file.name} file uploaded successfully.`);
      } else if (status === 'error') {
        message.error(`${info.file.name} file upload failed.`);
      }
    };

    // 4️⃣ 处理文件拖拽事件
    function handleDrop(e) {
      // 获取拖拽的文件
      const files = e.dataTransfer.files;
      if (files.length > 0) {
        // 使用 FormData 来构建上传的文件数据
        const formData = new FormData();
        for (let file of files) {
          formData.append('file', file);
        }
        console.log(formData);

        // 上传文件到 Directus
        client.request(uploadFiles(formData)).then(response => {
          // // 上传成功后显示文件 ID
          // uploadStatus.value = `Upload successful! File ID: ${response[0].title}`;
          message.success('Files uploaded successfully!');
          console.log(response);
        }).catch(error => {
          // 上传失败处理
          uploadStatus.value = 'Upload failed!';
          message.error('File upload failed!');
          console.error(error);
        });
      }
    }
    const __returned__ = {
      client,
      fileList,
      get uploadStatus() {
        return uploadStatus;
      },
      set uploadStatus(v) {
        uploadStatus = v;
      },
      handleChange,
      handleDrop,
      ref,
      get message() {
        return message;
      },
      get InboxOutlined() {
        return InboxOutlined;
      },
      get createDirectus() {
        return createDirectus;
      },
      get rest() {
        return rest;
      },
      get uploadFiles() {
        return uploadFiles;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
