import "core-js/modules/es.array.with.js";
import { ref } from 'vue';
import { message } from 'ant-design-vue';
import { InboxOutlined } from '@ant-design/icons-vue';
import { createDirectus, rest, uploadFiles } from '@directus/sdk';

// 1️⃣ 创建 Directus 客户端

export default {
  __name: 'ocr',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const client = createDirectus('http://localhost:8055').with(rest());

    // 2️⃣ 初始化文件列表和上传状态
    const fileList = ref([]);
    let uploadStatus = ref('');

    // 3️⃣ 文件上传成功/失败时的处理
    const handleChange = info => {
      const status = info.file.status;
      if (status !== 'uploading') {
        console.log(info.file, info.fileList);
      }
      if (status === 'done') {
        message.success(`${info.file.name} file uploaded successfully.`);
      } else if (status === 'error') {
        message.error(`${info.file.name} file upload failed.`);
      }
    };

    // 4️⃣ 处理文件拖拽事件
    function handleDrop(e) {
      // 获取拖拽的文件
      const files = e.dataTransfer.files;
      if (files.length > 0) {
        // 使用 FormData 来构建上传的文件数据
        const formData = new FormData();
        for (let file of files) {
          formData.append('file', file);
        }
        console.log(formData);
        // 确保 imageUrl 是 Vue 响应式的 ref 类型
        const imageUrl = ref('');

        // 上传文件处理逻辑
        client.request(uploadFiles(formData)).then(response => {
          console.log('Response:', response.filename_disk); // 检查 response 数据结构

          // 确保 response 是一个数组，并且包含文件信息
          if (response && response.length > 0) {
            const file = response[0];

            // 检查 file 是否有效并且包含 filename_disk
            if (file && file.filename_disk) {
              // 构建文件的访问 URL
              const imageUrlPath = `http://localhost:8055/assets/${file.filename_disk}`;

              // 使用 .value 来赋值，因为 imageUrl 是一个 ref 类型
              imageUrl.value = imageUrlPath;

              // 更新上传状态
              uploadStatus.value = `Upload successful! File ID: ${file.id}`;
              message.success('File uploaded successfully!');
            } else {
              console.error('File information is missing or invalid.');
            }
          } else {
            console.error('No file returned in response.');
          }
        }).catch(error => {
          // 上传失败处理
          uploadStatus.value = 'Upload failed!';
          message.error('File upload failed!');
          console.error(error);
        });
      }
    }
    const __returned__ = {
      client,
      fileList,
      get uploadStatus() {
        return uploadStatus;
      },
      set uploadStatus(v) {
        uploadStatus = v;
      },
      handleChange,
      handleDrop,
      ref,
      get message() {
        return message;
      },
      get InboxOutlined() {
        return InboxOutlined;
      },
      get createDirectus() {
        return createDirectus;
      },
      get rest() {
        return rest;
      },
      get uploadFiles() {
        return uploadFiles;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
