import { reactive, h, ref, onMounted, watch } from 'vue';
import { RouterLink } from 'vue-router';
import { PieChartOutlined, MailOutlined, MenuFoldOutlined, MenuUnfoldOutlined } from '@ant-design/icons-vue';

// 左侧菜单和折叠状态

export default {
  __name: 'Layout',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const state = reactive({
      collapsed: false,
      selectedKeys: ['1'],
      openKeys: ['sub1'],
      preOpenKeys: ['sub1']
    });

    // 定义菜单项
    const items = reactive([{
      key: 'sub1',
      icon: () => h(MailOutlined),
      label: '产品管理',
      children: [{
        key: '1',
        label: '产品信息',
        route: '/productInfo' // 设置路由
      }, {
        key: '2',
        label: 'Option 6',
        route: '/option-6' // 设置路由
      }]
    }, {
      key: '3',
      icon: () => h(PieChartOutlined),
      label: '文字提取',
      route: '/ocr' // 设置路由
    }]);

    // 动态展开的菜单
    watch(() => state.openKeys, (_val, oldVal) => {
      state.preOpenKeys = oldVal;
    });

    // 处理折叠菜单
    const toggleCollapsed = () => {
      state.collapsed = !state.collapsed;
      state.openKeys = state.collapsed ? [] : state.preOpenKeys;
    };
    const __returned__ = {
      state,
      items,
      toggleCollapsed,
      reactive,
      h,
      ref,
      onMounted,
      watch,
      get RouterLink() {
        return RouterLink;
      },
      get PieChartOutlined() {
        return PieChartOutlined;
      },
      get MailOutlined() {
        return MailOutlined;
      },
      get MenuFoldOutlined() {
        return MenuFoldOutlined;
      },
      get MenuUnfoldOutlined() {
        return MenuUnfoldOutlined;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
